import { useEffect, useState } from "react";
import axios from "axios";

export default function Tipos({ onLogout }) {
  const [tipos, setTipos] = useState([]);
  const [form, setForm] = useState({ nombre: "" });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchTipos();
  }, []);

  const fetchTipos = () => {
    axios.get("http://localhost:4000/api/tipos")
      .then(res => setTipos(res.data));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (editingId) {
      axios.put(`http://localhost:4000/api/tipos/${editingId}`, form)
      .then(() => { fetchTipos(); resetForm(); });
    } else {
      axios.post("http://localhost:4000/api/tipos", form)
      .then(() => { fetchTipos(); resetForm(); });
    }
  };

  const eliminar = (id) => {
    axios.delete(`http://localhost:4000/api/tipos/${id}`)
      .then(() => fetchTipos());
  };

  const editar = (t) => {
    setForm({ nombre: t.nombre });
    setEditingId(t.id);
  };

  const resetForm = () => {
    setForm({ nombre: "" });
    setEditingId(null);
  };

  return (
    <div style={{ padding: "20px" }}>
      <button
        onClick={onLogout}
        style={{
          background: "#e74c3c",
          color: "white",
          border: "none",
          padding: "10px 15px",
          borderRadius: "8px",
          cursor: "pointer",
          marginBottom: "15px"
        }}
      >
        ðŸ”™ Cerrar SesiÃ³n
      </button>

      <h2>ðŸ“¦ AdministraciÃ³n de Tipos</h2>

      <form onSubmit={handleSubmit}>
        <input
          placeholder="Nombre del tipo"
          value={form.nombre}
          onChange={(e) => setForm({ nombre: e.target.value })}
          required
        />

        <button type="submit">
          {editingId ? "Actualizar" : "Crear"}
        </button>
      </form>

      <table border="1" cellPadding="10">
        <thead>
          <tr>
            <th>Tipo</th>
            <th>Acciones</th>
          </tr>
        </thead>

        <tbody>
          {tipos.map(t => (
            <tr key={t.id}>
              <td>{t.nombre}</td>
              <td>
                <button onClick={() => editar(t)}>Editar</button>
                <button onClick={() => eliminar(t.id)}>Eliminar</button>
              </td>
            </tr>
          ))}
        </tbody>

      </table>
    </div>
  );
}
