import { useEffect, useState } from "react";
import axios from "axios";

export default function Productos({ onLogout }) {
  const [productos, setProductos] = useState([]);
  const [tipos, setTipos] = useState([]);
  const [form, setForm] = useState({ nombre: "", precio: "", tipo_id: "" });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchProductos();
    fetchTipos();
  }, []);

  const fetchProductos = () => {
    axios
      .get("http://localhost:4000/api/productos")
      .then((res) => setProductos(res.data));
  };

  const fetchTipos = () => {
    axios.get("http://localhost:4000/api/productos").then((res) => {
      const tiposUnicos = [
        ...new Map(
          res.data.map((item) => [
            item["tipo_id"],
            { id: item.tipo_id, nombre: item.tipo },
          ])
        ).values(),
      ];
      setTipos(tiposUnicos);
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editingId) {
      axios
        .put(`http://localhost:4000/api/productos/${editingId}`, form)
        .then(() => {
          fetchProductos();
          resetForm();
        });
    } else {
      axios
        .post("http://localhost:4000/api/productos", form)
        .then(() => {
          fetchProductos();
          resetForm();
        });
    }
  };

  const eliminar = (id) => {
    axios
      .delete(`http://localhost:4000/api/productos/${id}`)
      .then(() => fetchProductos());
  };

  const editar = (prod) => {
    setForm({
      nombre: prod.nombre,
      precio: prod.precio,
      tipo_id: prod.tipo_id,
    });
    setEditingId(prod.id);
  };

  const resetForm = () => {
    setForm({ nombre: "", precio: "", tipo_id: "" });
    setEditingId(null);
  };

  return (
    <div
      style={{
        background: "white",
        padding: "30px",
        borderRadius: "12px",
        maxWidth: "900px",
        margin: "0 auto",
        boxShadow: "0px 4px 15px rgba(0,0,0,0.1)",
      }}
    >
      {/* BOT√ìN CERRAR SESI√ìN */}
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          marginBottom: "15px",
        }}
      >
        <button
          onClick={onLogout}
          style={{
            background: "#e74c3c",
            color: "white",
            border: "none",
            padding: "10px 18px",
            borderRadius: "8px",
            cursor: "pointer",
            fontSize: "15px",
            transition: "0.3s",
          }}
          onMouseOver={(e) => (e.target.style.background = "#c0392b")}
          onMouseOut={(e) => (e.target.style.background = "#e74c3c")}
        >
          üîô Cerrar sesi√≥n
        </button>
      </div>

      <h2
        style={{
          textAlign: "center",
          marginBottom: "25px",
          color: "#333",
          fontSize: "28px",
        }}
      >
        üçî Administraci√≥n de Productos
      </h2>

      {/* FORMULARIO */}
      <form
        onSubmit={handleSubmit}
        style={{
          display: "grid",
          gridTemplateColumns: "1fr 1fr 1fr auto",
          gap: "10px",
          marginBottom: "25px",
        }}
      >
        <input
          placeholder="Nombre"
          value={form.nombre}
          onChange={(e) => setForm({ ...form, nombre: e.target.value })}
          required
          style={{
            padding: "10px",
            borderRadius: "8px",
            border: "1px solid #ccc",
          }}
        />

        <input
          type="number"
          placeholder="Precio"
          value={form.precio}
          onChange={(e) => setForm({ ...form, precio: e.target.value })}
          required
          style={{
            padding: "10px",
            borderRadius: "8px",
            border: "1px solid #ccc",
          }}
        />

        <select
          value={form.tipo_id}
          onChange={(e) => setForm({ ...form, tipo_id: e.target.value })}
          required
          style={{
            padding: "10px",
            borderRadius: "8px",
            border: "1px solid #ccc",
          }}
        >
          <option value="">Tipo de producto</option>
          {tipos.map((tipo) => (
            <option key={tipo.id} value={tipo.id}>
              {tipo.nombre}
            </option>
          ))}
        </select>

        <button
          type="submit"
          style={{
            background: "#27ae60",
            color: "white",
            padding: "10px",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          {editingId ? "Actualizar" : "Crear"}
        </button>
      </form>

      {/* TABLA */}
      <table
        style={{
          width: "100%",
          borderCollapse: "collapse",
        }}
      >
        <thead>
          <tr
            style={{
              background: "#f39c12",
              color: "white",
            }}
          >
            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
              Producto
            </th>
            <th style={{ padding: "10px", border: "1px solid #ddd" }}>Tipo</th>
            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
              Precio
            </th>
            <th style={{ padding: "10px", border: "1px solid #ddd" }}>
              Acciones
            </th>
          </tr>
        </thead>

        <tbody>
          {productos.map((p) => (
            <tr key={p.id}>
              <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                {p.nombre}
              </td>
              <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                {p.tipo}
              </td>
              <td style={{ padding: "10px", border: "1px solid #ddd" }}>
                ${p.precio}
              </td>
              <td
                style={{
                  padding: "10px",
                  border: "1px solid #ddd",
                  display: "flex",
                  gap: "10px",
                }}
              >
                <button
                  onClick={() => editar(p)}
                  style={{
                    background: "#2980b9",
                    color: "white",
                    border: "none",
                    padding: "8px 12px",
                    borderRadius: "6px",
                    cursor: "pointer",
                  }}
                >
                  ‚úèÔ∏è Editar
                </button>

                <button
                  onClick={() => eliminar(p.id)}
                  style={{
                    background: "#c0392b",
                    color: "white",
                    border: "none",
                    padding: "8px 12px",
                    borderRadius: "6px",
                    cursor: "pointer",
                  }}
                >
                  ‚ùå Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}
